import React, { useState } from 'react';
import axios from 'axios';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './GitHubPRPage.css';
// import { useNavigate } from 'react-router';

function GitHubPRPage() {
  const [repoUrl, setRepoUrl] = useState('');
  const [pullRequests, setPullRequests] = useState([]);
const[comments, setComments] = useState([]);


  const handleRepoUrlChange = (e) => {
    setRepoUrl(e.target.value);
  };

  const fetchCommentRequest = async () => {
    // window.location.href = repoUrl
      try {
       let commentData = repoUrl.replace("https://github.com" , "https://api.github.com/repos/comments")
        const response = await axios.get(commentData);
        setComments(response.data);
      } 
      catch (error) {
        console.error(error);
      }
    };

  const fetchPullRequests = async () => {
  // window.location.href = repoUrl
    try {
     let repoData = repoUrl.replace("https://github.com" , "https://api.github.com/repos")
      const response = await axios.get(repoData);
      setPullRequests(response.data);
    } 
    catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="github-pr-page">
      <h1>GitHub Pull Requests</h1>
      <input
        type="text"
        placeholder="Enter GitHub Repo URL"
        value={repoUrl}
        onChange={handleRepoUrlChange}
      />
      <button onClick={fetchCommentRequest}>Fetch PRs</button>
      <div className="pull-requests">
        {pullRequests.map((pr) => (
          <div key={pr.id} className="pr-card">
            <h2>{pr.title}</h2>
            <p>Author: {pr.user.login}</p>
            {comments.map((cm)=>
            (
              <p>Comments: {cm.comments}</p>
            ))}
            {/* <p>Comments: {pr.comments}</p> */}
            <Carousel showArrows={true} showThumbs={false}>
              <div className = "image">
                <img src={pr.user.avatar_url} alt={`${pr.user.login}'s avatar`} />
              </div>
            </Carousel>
          </div>
        ))}
      </div>
    </div>
  );
}

export default GitHubPRPage;
