import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './GitHubPRPage.css';
// import { useNavigate } from 'react-router';

function GitHubPRPage() {
  const [repoUrl, setRepoUrl] = useState('');
  const [pullRequests, setPullRequests] = useState([]);



  const handleRepoUrlChange = (e) => {
    setRepoUrl(e.target.value);
  };

  const fetchPullRequests = async () => {
    // window.location.href = repoUrl
    try {
      let repoData = repoUrl.replace("https://github.com", "https://api.github.com/repos")
      const response = await axios.get(repoData);
      setPullRequests(response.data);
    }
    catch (error) {
      console.error(error);
    }
  };

  const renderPr = (pr) => {
    const [count,setCount] = useState(0);
    useEffect(()=>{
      axios.get(pr.comments_url).then((res)=>{
        console.log(res,"=-=-=-");
        // setCount(res);
      }).catch((err)=>{

      })
    },[])
    return <div key={pr.id} className="pr-card">
      <h2>{pr.title}</h2>
      <p>Author: {pr.user.login}</p>
      <p>Comments: {pr.comments}</p>
      <a href={pr.comments_url}>{pr.comments_url}</a>
      <Carousel showArrows={true} showThumbs={false}>
        <div className="image">
          <img src={pr.user.avatar_url} alt={`${pr.user.login}'s avatar`} />
        </div>
      </Carousel>
    </div>
  }

  return (
    <div className="github-pr-page">
      <h1>GitHub Pull Requests</h1>
      <input
        type="text"
        placeholder="Enter GitHub Repo URL"
        value={repoUrl}
        onChange={handleRepoUrlChange}
      />
      <button onClick={fetchPullRequests}>Fetch PRs</button>
      <div className="pull-requests">
        {pullRequests.map((pr) => (
          renderPr(pr)
        ))}
      </div>
    </div>
  );
}

export default GitHubPRPage;
